using System.Collections;
using System.Collections.Generic;
using UnityEngine;
using System;
using UnityEngine.UI;
using System.Runtime.InteropServices;

public class ProfileManager : MonoBehaviour
{
    [DllImport("__Internal")]
    private static extern void GetPlayerData();
    [DllImport("__Internal")]
    private static extern void GetOnlinePlayers();

    public Image profile;
    public Text playerName, rating, win, draw, loses;

    public GameObject invitationPanel, replyPanel, loadingPanel, exitPanel;

    void Start()
    {
#if UNITY_WEBGL && !UNITY_EDITOR
        GetPlayerData();
        GetOnlinePlayers();
#else
        showList("pylon.elem.student@gmail.com|Pylon E. Student,pylon.hs.student@gmail.com | Pylon H.Student,pylon.sh.student@gmail.com | Pylon S.Student");
        setPlayerData("Pylon S. BPESchool|Grade 4 - Section A|");
#endif

    }

    public void setPlayerData(string playerData)
    {
        string[] data = playerData.Split('|');
        string name = data[0];
        string section = data[1];
        string image = data[2];

        byte[] imageBytes = Convert.FromBase64String(image);
        Texture2D tex = new Texture2D(2, 2);
        tex.LoadImage(imageBytes);
        Sprite sprite = Sprite.Create(tex, new Rect(0.0f, 0.0f, tex.width, tex.height), new Vector2(0.5f, 0.5f), 100.0f);

        // Set Data
        playerName.text = name;
        profile.sprite = sprite;

          
    }

    public void showList(string onlineList)
    {
        Matching matching = this.transform.parent.GetChild(3).GetComponent<Matching>();

        foreach (Transform child in matching.playerList)
        {
            Destroy(child.gameObject);
        }

        if (onlineList.Length > 0)
        {
            string[] list = onlineList.Split(',');

            foreach (string player in list)
            {
                string[] playerInfo = player.Split('|');

                //print(playerInfo[0]); email
                // print(playerInfo[1]); name

                var online = Resources.Load("Button") as GameObject;
                var onlineInfo = Instantiate(online, matching.playerList);

                string[] fName = playerInfo[1].Split(' ');
                onlineInfo.transform.GetChild(0).GetComponent<Text>().text = fName[0];

                onlineInfo.GetComponent<playersData>().playersName = playerInfo[1];
                onlineInfo.GetComponent<playersData>().email = playerInfo[0];
            }
        }        
    }

    public void invite(string data)
    {
        invitationPanel.SetActive(true);

        invitationPanel.GetComponent<inivtationManager>().setInviterData(data);
    }

    public void exit()
    {
        exitPanel.SetActive(true);
    }

    public void load()
    {
        StartCoroutine(loading());
    }

    public IEnumerator loading()
    {
        yield return new WaitForSeconds(1.40f);
        replyPanel.SetActive(false);
        loadingPanel.SetActive(true);

        yield return new WaitForSeconds(.3f);
        this.GetComponent<CanvasGroup>().alpha = 0;
        this.transform.parent.GetChild(3).gameObject.SetActive(false);

        ChessGameController gameController = GameObject.Find("GameController").GetComponent<ChessGameController>();

        //  gameController.Starting();

        GameObject chessBoard = GameObject.Find("ChessBoard");
        chessBoard.transform.GetChild(0).gameObject.SetActive(true);

        yield return new WaitForSeconds(.8f);
        loadingPanel.SetActive(false);
        this.gameObject.SetActive(false);
        this.transform.parent.gameObject.SetActive(false);
    }
}
